#Question n°1
def journalist_count(journalist)
    puts "> Il y a #{journalist.length} journalistes."
    puts " "
end

#Question n°2
def journalist_min(journalist)
    puts "> Le journaliste avec le nom plus court est : #{journalist.min {|a, b| a.length <=> b.length}}"
    puts " "
end

#Question n°3
def journalist_five(journalist)
    journalist.to_s
    result = journalist.select {|word| word.length == 6}.length
    puts "> Il y a #{result} journalistes dans notre base de données contenant 5 caractères."
    puts " "
end

#Question n°4
def journalist_maj(journalist)
    puts "> il y a #{journalist.grep(/@[A-Z]/).count} journalistes qui ont une majuscule après @"
    puts " "
end

#Question n°5
def journalist_alpha(journalist)
    puts "> Voici la liste des journalistes dans l'odre alphabetique"
    puts journalist.sort
end

#Question n°6
def journalist_size(journalist)
    #name_size = journalist.sort_by{|word| word.length}
    name_size = journalist.sort_by{|word| word.length}
    puts "> La liste suivant la taille (du plus petit au plus grand) :"
    puts name_size
    puts " "
end

#Question n°7
def journalist_find(journalist)
    position_dB = journalist.index("@epenser")
    puts "> La personne '@epenser' est positionnée dans la data base au : #{position_dB}"
    puts " "
end

#Question n°8
def journalist_spread(journalist)
    handle_length = journalist.map {|handle| handle.size.to_i}
    handle_length = handle_length.sort
    a = handle_length.first
    z = handle_length.last
    for i in a..z do
        handle_size = journalist.count {|handle| handle.size == i}
        if handle_size !=0
            puts "nombre de handles avec #{i} caractères est de #{handle_size} "
            puts " "
        end
    end
end




def perform
    journalist_count(data)
    journalist_min(data)
    journalist_five(data)
    journalist_maj(data)
    journalist_alpha(data)
    journalist_size(data)
    journalist_find(data)
    journalist_spread(data)
end

perform







